		tst.b   ($FFFFFFD1).w
		beq.s   Pause_CheckStart
		btst    #$06, (Control_Ports_Buffer_Data+$0001).w
		beq.s   Pause_CheckBC
		move.b  #gm_TitleScreen, (Game_Mode).w
		nop
		bra.s   loc_1588
Pause_CheckBC:
		btst    #$04, (Control_Ports_Buffer_Data).w
		bne.s   Pause_SlowMotion
		btst    #$05, (Control_Ports_Buffer_Data+$0001).w
		bne.s   Pause_SlowMotion
