		btst    #$07, (Control_Ports_Buffer_Data+$0001).w
		beq.s   Pause_Loop
loc_1588:
		move.b  #$FF, (Sound_Buffer_Id).w
Unpause:
		move.w  #$0000, (Pause_Status).w
Pause_DoNothing:
		rts
Pause_SlowMotion:
		move.w  #$0001, (Pause_Status).w
		move.b  #$FF, (Sound_Buffer_Id).w
		rts
