;===============================================================================
; Sound driver stuff
; [ Begin ]
;===============================================================================
SoundDriverLoad:
		move    SR, -(A7)
		movem.l D0-D7/A0-A6, -(A7)
		move    #$2700, SR
		lea     (Z80_Bus_Request), A3
		lea     (Z80_Reset), A2
		moveq   #$00, D2
		move.w  #$0100, D1
		move.w  D1, (A3)
		move.w  D1, (A2)
loc_EC020:
		btst    D2, (A3)
		bne.s   loc_EC020
		jsr     loc_EC03C(PC)
		move.w  D2, (A2)
		move.w  D2, (A3)
		moveq   #$17, D0
loc_EC02E:
		dbra    D0, loc_EC02E
		move.w  D1, (A2)
		movem.l (A7)+, D0-D7/A0-A6
		move    (A7)+, SR
		rts
;-------------------------------------------------------------------------------
loc_EC03C:
		lea     Z80_Sound_Driver(PC), A6
		move.w  #$0E7E, D7
		moveq   #$00, D6
		lea     (Z80_RAM_Start), A5
		moveq   #$00, D5
		lea     (Z80_RAM_Start), A4
loc_EC054:
		lsr.w   #$01, D6
		btst    #$08, D6
		bne.s   loc_EC066
		jsr     loc_EC0D2(PC)
		move.b  D0, D6
		ori.w   #$FF00, D6
loc_EC066:
		btst    #$00, D6
		beq.s   loc_EC078
		jsr     loc_EC0D2(PC)
		move.b  D0, (A5)+
		addq.w  #$01, D5
		bra     loc_EC054
loc_EC078:
		jsr     loc_EC0D2(PC)
		moveq   #$00, D4
		move.b  D0, D4
		jsr     loc_EC0D2(PC)
		move.b  D0, D3
		andi.w  #$000F, D3
		addq.w  #$02, D3
		andi.w  #$00F0, D0
		lsl.w   #$04, D0
		add.w   D0, D4
		addi.w  #$0012, D4
		andi.w  #$0FFF, D4
		move.w  D5, D0
		andi.w  #$F000, D0
		add.w   D0, D4
		cmp.w   D4, D5
		bcc.s   loc_EC0C0
		subi.w  #$1000, D4
		bcc.s   loc_EC0C0
		add.w   D3, D5
		addq.w  #$01, D5
loc_EC0B2:
		move.b  #$00, (A5)+
		addq.w  #$01, D4
		dbra    D3, loc_EC0B2
		bra     loc_EC054
loc_EC0C0:
		add.w   D3, D5
		addq.w  #$01, D5
loc_EC0C4:
		move.b  $00(A4, D4), (A5)+
		addq.w  #$01, D4
		dbra    D3, loc_EC0C4
		bra     loc_EC054
;-------------------------------------------------------------------------------
loc_EC0D2:
		move.b  (A6)+, D0
		subq.w  #$01, D7
		bne.s   loc_EC0DA
		addq.w  #$04, A7
loc_EC0DA:
		rts
